import React,{useState} from 'react';

const Input = (props) => {
    let {type, placeholder, text} = props;
    
    return(
        <input type={type} placeholder={placeholder} text={text ? text : ""} 
            className={className}
            style={{margin:"20px"}}
            onBlur={(e) => {validation(type,e.target.value)}}>
        </input>
    );
}
function validation(type,e){
    switch(type)
    {
        case "number":
            alert(e);
            if(isNaN(e))
                alert("pirla");
            break;
    }
}
export default Input;
