import React from 'react';
import { ListGroupItemHeading, Row } from 'reactstrap';
import Input from  '../InputsComponents/Input';
const Consumi = () =>{
    let kmTot=0;
    let litriTot=0;
    const kmIniziali = 30;

    let listaConsumi = [
        {
            km : 400,
            litri : 32.7,
            commento : "fuzinona"
        },
        {
            km : 500,
            litri : 33.7,
            commento : "fuzinona"
        },
        {
            km : 560,
            litri : 2345.7,
            commento : "funziona"
        },
    ];

    kmTot = listaConsumi.reduce((returnValue,lista) => returnValue += lista.km, -kmIniziali);
    litriTot = listaConsumi.reduce((returnValue,lista) => returnValue += lista.litri, 0);

    return(
        <>
        <DivInserisciDati listaConsumi = {listaConsumi}/>
        <div className={"card m-3 text-center"}>
            <div className={"card-body row"}>
                <div className={"col-sm-4"}>
                   <span>KM totali percorsi: {kmTot}</span>
                </div>
                <div className={"col-sm-4"}>
                    <span>Litri totali usati: {litriTot}</span>
                </div>
                <div className={"col-sm-4"}>
                    <span>Media consumi: {kmTot/litriTot}</span>
                </div>
            </div>
        </div>
        {
            listaConsumi.map((value)=> {
            let { km, litri, commento } = value;
            return(<DivConsumo km={km} litri={litri} commento={commento}/>)
            })
        }
        </>
    );
}

const DivConsumo = (props) =>
{
    const {km, litri, commento} = props;

    return(
    <>
        <div className={"card m-3 text-center"}>
            <div className={"card-body row"}>
                <div className={"col-sm-4"}>
                   <span>km segnati: {km}</span>
                </div>
                <div className={"col-sm-4"}>
                    <span>litri caricati: {litri}</span>
                </div>
                <div className={"col-sm-4"}>
                    <span>commento: {commento}</span>
                </div>
            </div>
        </div>
    </>
    );
}

const DivInserisciDati = (props) =>{
    let lista =  props.listaConsumi;
    let obj = {};
    return(
        <>
            <div className={"card m-3 text-center"}>
                <div className={"card-body row"}>
                    <div className={"row"}>
                        <div className={"col-sm-3 text-center"}>
                        <span>KM segnati: <input onChange={(e)=>obj.km=e.target.value}></input></span>
                        </div>
                        <div className={"col-sm-3"}>
                            <span>Litri caricati: <input onChange={(e)=>obj.litri=e.target.value}></input></span>
                        </div>
                        <div className={"col-sm-3"}>
                            <span>Spesa: <input onChange={(e)=>obj.commento=e.target.value}></input></span>
                        </div>
                        <div className={"col-sm-3"}>
                            <span>Commento: <input onChange={(e)=>obj.commento=e.target.value}></input></span>
                        </div>
                    </div>
                    <div className={"col-sm-12 text-center m-2"}><button onClick={()=>this.setState({ lista: [...this.state.lista, obj] })} className={"btn btn-primary btn-lg"}>Inserisci</button></div>  
                </div>
            </div>
        </>
        );
}

function addToList(lista, oggetto)
{
    
}
export default Consumi;
