const PrimaComponent = (props) => 
{
    let list=
    [
       {
          foto:"https://www.motorimagazine.it/wp-content/uploads/2020/05/lamborghini-aventador-svj_dietro-e1589548406883.jpg",
          modello:"Lamborghini aventador SVJ",
          dimensione:"col-md-4"
       },
       {
          foto:"https://immagini.alvolante.it/sites/default/files/styles/anteprima_640/public/prossima_uscita_anteprima/2013/11/opel-meriva-restyling_3.jpg?itok=65vRcIOL",
          modello:"Opel meriva",
          dimensione:"col-md-4"
       },
       {
          foto:"https://rentedrive.it/wp-content/uploads/2020/03/Toyota-Yaris-2020-Esterno-posteriore.jpg",
          modello:"Toyota yaris",
          dimensione:"col-md-4"
       }
    ];
    return(
        <>
            <div className="row" style={{marginTop:"5%", padding:"5px"}}>
                {
                    list.map((value)=>
                    {
                       let {foto,modello,dimensione} = value;
                       return(
                       <div className={dimensione}> 
                            <h1>Foto</h1>
                            <ImageComponent foto={foto} modello={modello}/>
                       </div>);
                    })
                }
            </div>
        </>
    );
}

const ImageComponent = (props) =>
{
    let {foto,modello,dimensione} = props;
    const titolo = <h1>Modello: {modello}</h1>
                    ;
    return(
        <>
            {titolo}
            <div style={{padding:"20px",border:"1px solid black", display:"inline-block"}}>
                <img src={foto} style={{maxWidth:"100%"}}></img>
            </div>
        </>
    );
}

export default PrimaComponent;
