const Input = (props) => {
    let {type, placeholder, text, canbenull} = props;
    let className = "";
    return(
        <input type={type} placeholder={placeholder} text={text ? text : ""} 
            className={className}
            style={{margin:"20px"}}
            onfocusout="validation(type)"
            id="inputbox">
        </input>
    );
}
function validation(type){
    var x = document.getElementById("inputbox");
    switch(type)
    {
        case number:
            if(isNaN(x))
                alert("pirla");
                x=""
            break;
    }
}
export default Input;
