import './App.css';
import React,{useState} from 'react';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink
  } from 'reactstrap';
import Consumi from './basicComponent/Consumi';
import Manutenzione from './basicComponent/Manutenzione';

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [div, setDiv] = useState(0);
  const toggle = () => setIsOpen(!isOpen);
  const SwitchState = (numDiv) => (setDiv(numDiv));
  let auto
  let list=
  [
     {
        foto:"https://www.motorimagazine.it/wp-content/uploads/2020/05/lamborghini-aventador-svj_dietro-e1589548406883.jpg",
        modello:"Lamborghini aventador",
        dimensione:"col-md-4"
     },
     {
        foto:"https://immagini.alvolante.it/sites/default/files/styles/anteprima_640/public/prossima_uscita_anteprima/2013/11/opel-meriva-restyling_3.jpg?itok=65vRcIOL",
        modello:"Opel meriva",
        dimensione:"col-md-4"
     },
     {
        foto:"https://rentedrive.it/wp-content/uploads/2020/03/Toyota-Yaris-2020-Esterno-posteriore.jpg",
        modello:"Toyota yaris",
        dimensione:"col-md-4"
     }
  ];
  return (
    <>
    <Navbar color="dark" dark style={{padding:"20px"}} expand="md">
      <NavbarBrand onClick={()=>SwitchState(0)}>Super react</NavbarBrand>
      <NavbarToggler onClick={toggle} />
      <Collapse isOpen={isOpen} navbar>
        <Nav className="mr-auto" navbar>
          <NavItem>
              <NavLink onClick={()=>SwitchState(1)} href="#">Consumi</NavLink>
          </NavItem>
          <NavItem>
            <NavLink onClick={()=>SwitchState(2)} href="#">Manutenzione</NavLink>
          </NavItem>
          <NavItem>
            <NavLink onClick={()=>SwitchState(3)}>Altro</NavLink>
          </NavItem>
        </Nav>
      </Collapse>
    </Navbar>
    <div className="container-fluid">
    <br/>
    {div===1 &&  <Consumi/>}
    {div===2 &&  <Manutenzione/>}
    </div>
    </>
  );
}



export default App;
